$('.example-p-2').on('click', function(){
  $.confirm({
      title: 'A secure action',
      content: 'Its smooth to do multiple confirms at a time. <br> Click confirm or cancel for another modal',
      icon: 'fa fa-question-circle',
      animation: 'scale',
      closeAnimation: 'scale',
      opacity: 0.5,
      buttons: {
          'confirm': {
              text: 'Proceed',
              btnClass: 'btn-blue',
              action: function(){
                  $.confirm({
                      title: 'This maybe critical',
                      content: 'Critical actions can have multiple confirmations like this one.',
                      icon: 'fa fa-warning',
                      animation: 'scale',
                      closeAnimation: 'zoom',
                      buttons: {
                          confirm: {
                              text: 'Yes, sure!',
                              btnClass: 'btn-orange',
                              action: function(){
                                  $.alert('A very critical action <strong>triggered!</strong>');
                              }
                          },
                          cancel: function(){
                              $.alert('you clicked on <strong>cancel</strong>');
                          }
                      }
                  });
              }
          },
          cancel: function(){
              $.alert('you clicked on <strong>cancel</strong>');
          },
          moreButtons: {
              text: 'something else',
              action: function(){
                  $.alert('you clicked on <strong>something else</strong>');
              }
          },
      }
  });
});

document.addEventListener('DOMContentLoaded', function () {
  if (document.querySelectorAll('#mapa').length > 0)
  {
    if (document.querySelector('html').lang)
      lang = document.querySelector('html').lang;
    else
      lang = 'en';

    var js_file = document.createElement('script');
    js_file.type = 'text/javascript';
    js_file.src = 'https://maps.googleapis.com/maps/api/js?callback=initMap&signed_in=true&language=' + lang;
    document.getElementsByTagName('head')[0].appendChild(js_file);
  }
});

var map = null;
var labels = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
var markers = null;
var locations = [
        {lat: 19.4440685, lng: -99.1418708},
        {lat: 19.559797, lng: -99.0500927},
        {lat: 19.835556, lng: -98.9352197}
      ]

function initMap() {
    var options = {
        zoom: 11,
        center: {lat: 19.4440685, lng: -99.1418708},
    };

    map = new google.maps.Map(document.getElementById('mapa'), options);
    markers = locations.map(function(location, i) {
        return new google.maps.Marker({
        position: locations,
        label: labels[i % labels.length]
        });
    });
    var markerCluster = new MarkerClusterer(map, markers,
            {imagePath: 'https://developers.google.com/maps/documentation/javascript/examples/markerclusterer/m'});

}



